<?phpinclude("config.php");include("osm2geo.php");function getTileToJson($x1, $y1, $x2, $y2){	$baseUrl = Tile_config['url'];	$url = $baseUrl.$x1.','.$y1.','.$x2.','.$y2;		$bX = $Tile_config['width'];	$bY = $Tile_config['height'];		$offX = $x1 / $bX;	$offY = $y1 / $bY;			$opts = array(		'http'=>array(			'method'=>"GET",			'timeout'=>Tile_config['timeout'],		)	);	$context = stream_context_create($opts);			//try multitimes if timeout	$n=0;	while($n<10)	{		$osm = file_get_contents($url, false, $context);		$n++;	}		//********************** to json file *******************************	$xml = simplexml_load_string($osm);	$geojson = osm2geo($xml);	$result = json_encode($geojson)	file_put_contents("../data/tile_".$offX."_".$offY.".json", $result);//save file	return $result;}?>