<?phpfunction openDataDir($dir) //"../data"{	$folder = $dir;	//打开目录	$fp=opendir($folder);	//阅读目录	while(false!=$file=readdir($fp))	{		//列出所有文件并去掉'.'和'..'		if($file!='.' &&$file!='..')		{			//赋值给数组			$arr_file[] = $file;		}	}	//输出结果	if(is_array($arr_file))	{		echo count($arr_file);		while(list($key,$value)=each($arr_file))		{			// $path = "../data/$value";						// echo "$path   -----------  OK<br>";		}	}	//关闭目录	closedir($fp);}?>